INSERT INTO "public"."problems" ("id", "title", "description", "context", "time_limit_minutes", "difficulty", "is_active", "created_at", "ai_context") VALUES ('2e6d68f0-8dab-40ca-b33f-914f271e3afe', '15% Drop in iOS Checkout Completion', 'Investigate a platform-specific conversion drop that occurred without any recent app deployments.', 'You are the PM for a global E-commerce platform. Yesterday, your daily business report showed that overall Checkout Completion dropped by 4%. Upon initial investigation, you see that Android and Web conversion rates are flat (normal), but iOS conversion has dropped by 15% in the last 24 hours. No new app version was released to the App Store in the last 3 days, and your server-side logs show no increase in 500-level errors.', '15', 'Medium', 'true', '2026-01-02 17:23:56.800176+00', null), ('87ef4d36-2594-4d96-80bf-c46cb90dee1f', '30% Drop in Organic Search Traffic', 'Investigate a sudden collapse in top-of-funnel acquisition from search engines.', 'You are the Growth PM for a popular B2B SaaS tool. Organic search is your #1 acquisition channel. This morning, your SEO dashboard shows a 30% decline in organic sessions over the last 48 hours. Your site health checks are green, page load speeds are normal, and no new code has been deployed to the marketing site in the last week. The drop is affecting your highest-converting product pages.', '15', 'Hard', 'true', '2026-01-02 17:48:31.058669+00', null), ('90326aca-e5a4-4015-8ad8-0adf2d9165bc', '8% Decline in DAU despite High Notification Engagement', 'Investigate why user retention is dropping even though notification interactions are increasing.', 'You are the PM for a social commerce app. Over the last 14 days, your Daily Active Users (DAU) have dropped by 8%. Interestingly, your push notification Click-Through Rate (CTR) has increased by 20% in the last 24 hours after a new AI-driven notification engine was toggled on. While more users are clicking notifications, they are spending less time in the app once they land, and your 7-day retention is starting to slip.', '15', 'Medium', 'true', '2026-01-02 17:45:42.228588+00', null), ('c974000c-7fe1-42c2-8a0b-52852af863b6', 'Onboarding Funnel Drop', 'Investigate a gradual decline in completion rates for a consumer app.', 'You are a Product Manager owning the onboarding flow for a consumer-facing digital product. The journey consists of 4–5 steps (details, email, permissions, profile setup). Over the last 7 days, you’ve observed a gradual decline in overall completion rate, without any obvious product launch or announcement during this period.', '15', 'Medium', 'true', '2025-12-25 06:26:16.64944+00', null), ('ced5854a-923c-415f-b625-02cd7ce8c68b', '10% Drop in Successful Logins', 'Analyze a sudden spike in login failures involving a critical third-party authentication provider.', 'You are the PM for a high-growth Fintech app. Your primary acquisition and login method is "Sign in with Google." Over the last 2 hours, your Customer Support dashboard has exploded with tickets from users saying they are "stuck on a white screen" or seeing a "403 Error" when trying to log in. Your internal alerts show that Google Auth success rates have dropped from 99% to 89% globally. There were no internal deployments or configuration changes made by your team today.', '15', 'Hard', 'true', '2026-01-02 17:30:01.356208+00', null);